document.addEventListener("DOMContentLoaded",()=>{document.querySelectorAll(".passcode__eye").forEach(e=>{const t=e.previousElementSibling;!t||t.tagName!=="INPUT"||e.addEventListener("click",()=>{const s=t.type==="password";t.type=s?"text":"password",e.classList.toggle("active",s)})}),document.querySelectorAll(".plsdata__input").forEach(e=>{const t=e.closest(".plsdata");if(!t)return;const s=t.querySelector(".plsdata__content");if(!s)return;const a=()=>{s.classList.toggle("disable",e.value.trim()!=="")};e.addEventListener("focus",()=>{s.classList.add("disable")}),e.addEventListener("blur",a),e.addEventListener("input",a),a()})});
