document.querySelectorAll(".passcode__eye").forEach(e=>{e.addEventListener("click",()=>{const s=e.previousElementSibling,t=s.getAttribute("type")==="password";s.setAttribute("type",t?"text":"password"),e.classList.toggle("active",t)})}),document.addEventListener("DOMContentLoaded",()=>{document.querySelectorAll(".plsdata__input").forEach(e=>{const s=e.closest(".plsdata").querySelector(".plsdata__content");e.addEventListener("focus",()=>{s.classList.add("disable")}),e.addEventListener("blur",()=>{e.value.trim()===""&&s.classList.remove("disable")}),e.addEventListener("input",()=>{e.value.trim()!==""?s.classList.add("disable"):s.classList.remove("disable")})})});
