document.addEventListener("DOMContentLoaded",()=>{document.querySelectorAll(".plsdata__input").forEach(e=>{const a=e.closest(".plsdata");if(!a)return;const t=a.querySelector(".plsdata__content");if(!t)return;const s=()=>{t.classList.toggle("disable",e.value.trim()!=="")};e.addEventListener("focus",()=>{t.classList.add("disable")}),e.addEventListener("blur",s),e.addEventListener("input",s),s()})});
